import Loginimage from "./loginform/loginimage.js"
import {Form, Button} from 'react-bootstrap';
import Loginform from "./loginform/loginform.js"
import SignUpForm from "./loginform/signup.js"
import React, { useEffect, useState } from "react";
import style from "./loginform/loginform.module.css"

function LoginSignup() {
    const [have_account, setAccount] = useState(false);
    const [change_color, setColor] = useState(false);
    const changeState = (value) =>{
        setAccount(value)
        setColor(value)
    }
    return (

            <div className={style.outerDiv}>

            <div className={style.logindiv}>

            <div className={style.SignUpdiv}>

            <Loginimage/>


            <Button className={change_color? style.button_login:style.button_change } onClick={() => changeState(true)}>Login</Button>{' '}
            <Button className={change_color? style.button_login:style.button_change} onClick={() => changeState(false)}>Sign up</Button>{' '}

            <hr></hr><br/>
                {have_account?
                <Loginform/>:
                <SignUpForm/>
                    }
                </div>
           </div>
        </div>
    )
}

export default LoginSignup;
